# Read all English chunks and display them for translation
for i in range(2, 27):
    en_filename = f'chunks_en/tarot_chunk_{i:02d}.js'
    if os.path.exists(en_filename):
        with open(en_filename, 'r', encoding='utf-8') as f:
            content = f.read()
            print(f"\n--- Chunk {i} ---")
            # Parse and display just the card numbers
            start_idx = content.find('{')
            end_idx = content.rfind('}') + 1
            json_str = content[start_idx:end_idx]
            data = json.loads(json_str)
            card_nums = list(data.keys())
            print(f"Cards: {', '.join(card_nums)}")

